<script>
  export default {
    props: {
      detailInfoCard: {
        type: Object,
        required: true
      },
      darkTheme: {
        type: Boolean
      },
    },

    data() {
      return {
        numberOfDays: 3,
        daysArr: [],
        sun: ['Солнечно', 'Ясно'],
        cloudy: ['Облачно', 'Переменная облачность'],
        shower: ['Сильные ливни', 'Сильный дождь', 'Умеренный или сильный ливневый дождь', 'Умеренный дождь', 'Умеренный или сильный дождь со снегом'],
        lightRain: ['Слабая морось', 'Небольшой ливневый дождь', 'Местами дождь', 'Небольшой дождь', 'Временами умеренный дождь', 'Местами небольшой дождь', 'Небольшой дождь со снегом'],
        mainlyCloudy: ['Пасмурно'],
        storm: ['Местами грозы', 'В отдельных районах местами небольшой дождь с грозой'],
        fog: ['Дымка', 'Туман', 'Переохлажденный туман'],
        snow: ['Умеренный снег', 'Сильный снег', 'Небольшой снег', 'Местами снег'],
        snowstorm: ['Метель'],
      }
    },

    mounted() {
        let days = [ 'Пон', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб', 'Вс' ]
        let day = new Date().getDay();
        
        if (day == 5) this.daysArr = ['Сб', 'Вс', 'Пон'] 
        else if (day == 6) this.daysArr = ['Вс', 'Пон', 'Вт']
        else if (day == 7) this.daysArr = ['Пон', 'Вт', 'Ср']

        if (day < 5) {
          for (let i = 0; i < this.numberOfDays; i++) {
            this.daysArr.push(days[day + i])
          }
        }
    },

    methods: {
      weatherIcon(item, value) {
        return value.indexOf(item) !== -1
      },
    }
  }
</script>

